const slider=document.querySelector(".slider"),slides=slider.querySelectorAll(".slider-item"),dots=slider.querySelectorAll(".slider-dot"),sliderButtons=slider.querySelectorAll(".slider-btn");let currentSlide=0;const activeClassName="active";function sliderMove(e){e.preventDefault(),slides[currentSlide].classList.remove(activeClassName),dots[currentSlide].classList.remove(activeClassName),currentSlide=(currentSlide+1)%slides.length,slides[currentSlide].classList.add(activeClassName),dots[currentSlide].classList.add(activeClassName)}sliderButtons.forEach(e=>{e.addEventListener("click",sliderMove)});
