const slider=document.querySelector(".slider"),slides=slider.querySelectorAll(".slider-item"),dots=slider.querySelectorAll(".slider-dot"),sliderButtons=slider.querySelectorAll(".slider-btn");let currentSlide=0;const activeClassName="active";function sliderMove(e){e.preventDefault(),slides[currentSlide].classList.remove(activeClassName),dots[currentSlide].classList.remove(activeClassName),currentSlide=(currentSlide+1)%slides.length,slides[currentSlide].classList.add(activeClassName),dots[currentSlide].classList.add(activeClassName)}sliderButtons.forEach(e=>{e.addEventListener("click",sliderMove)});const mapLink=document.querySelector(".show-popup"),mapPopup=document.querySelector(".modal-map"),mapClose=mapPopup.querySelector(".modal-close");mapLink.addEventListener("click",function(){mapPopup.classList.add("modal-show")}),mapClose.addEventListener("click",function(e){e.preventDefault(),mapPopup.classList.remove("modal-show")});const formLink=document.querySelector(".button-show-popup"),formPopup=document.querySelector(".modal-form"),formClose=formPopup.querySelector(".modal-close");formLink.addEventListener("click",function(){formPopup.classList.add("modal-show")}),formClose.addEventListener("click",function(e){e.preventDefault(),formPopup.classList.remove("modal-show")});const buyLink=document.querySelectorAll(".buy-btn"),buyPopup=document.querySelector(".modal-order"),buyClose=buyPopup.querySelector(".modal-close"),buyCloseButton=buyPopup.querySelector(".order-esc-btn");for(var i=0;i<buyLink.length;i++)buyLink[i].addEventListener("click",function(e){e.preventDefault(),buyPopup.classList.add("modal-show")});buyClose.addEventListener("click",function(e){e.preventDefault(),buyPopup.classList.remove("modal-show")}),buyCloseButton.addEventListener("click",function(e){e.preventDefault(),buyPopup.classList.remove("modal-show")});
